import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const City = () => {
  const [cities, setCities] = useState([]);
  const [cityNom, setCityNom] = useState('');
  const [cityId, setCityId] = useState('');

  // Function to fetch the list of cities from the backend
  const fetchCities = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setCities(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to get a city by ID
  const getCityById = async (cityId) => {
    try {
      const response = await axios.get(`/ville/${cityId}`);
      const cit = cities.find((city) => city.id === cityId);
      setCityNom(response.data.nom);
      setCityId(response.data.id);
      document.getElementById('cityname').value = response.data.nom;
    } catch (error) {
      console.error(error);
    }
  };

  // Function to add a city
  const addCity = async () => {
    try {
      const response = await axios.post('/api/villes/save', { nom: cityNom });
      setCities([...cities, response.data]);
      setCityNom('');
    } catch (error) {
      console.error(error);
    }
    fetchCities();
  };

  // Function to delete a city
  const deleteCity = async (id) => {
    try {
      await axios.delete(`/api/villes/delete/${id}`);
      const updatedCities = cities.filter((city) => city.id !== id);
      setCities(updatedCities);
    } catch (error) {
      console.error(error);
    }
    fetchCities();
  };

  // Function to update a city
  const updateCity = async (cityId) => {
    try {
      const response = await axios.put(`/ville/update/${cityId}`, { nom: cityNom });
      const updatedCities = cities.map((city) => {
        if (city.id === response.data.id) {
          return response.data;
        }
        return city;
      });
      setCityId('');
      setCityNom('');
      setCities(updatedCities);
    } catch (error) {
      console.error(error);
    }
    fetchCities();
    document.getElementById('cityname').value = '';
  };

  useEffect(() => {
    fetchCities();
  }, []);

  return (
    <Fragment>


<div className="col py-3 d-flex align-items-center about-image"> 
            <div className="container">
                <div className="row">
                    <div className="col-lg-6 col-md-12 col-sm-12 d-flex align-items-center">
                        <div>
                            <h1 className="font-big text-red slide-in-top">À propos de moi</h1>
                            <hr></hr>
                            <p>
                            Je m'appelle Zakaria AMELLAL, étudiant en 4éme année cycle d'ingénieur à l'Ecole Marocaine des Sciences de l'ingénieur.
                            </p>
                            <p className='slide-in-bottom'>En tant qu'autonome challenger, je suis déterminé et motivé à atteindre mes objectifs. Je suis également engagé à continuellement apprendre et à travailler en équipe tout en étant capable de m'auto-évaluer. J'ai une forte capacité à résoudre les problèmes et à aborder les situations avec une perspective multi-facettes.
                            </p>
                            <p className='slide-in-bottom'>
                            Actuellement je suis à la recherche d'un stage d'étude d'une durrée de 2 mois.<br></br>
                            </p>              
                        </div>
                    </div>
                    <div className="col-lg-6 col-md-12 col-sm-12">
                        <img className="img-fluid" src="../images/nomad-amico.png" alt="..."></img>
                    </div>
                </div>
            </div>
        </div>




        <div className="container mt-4 slide-in-bottom">
        <h1>Cities</h1>
        <table className="table mt-3">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nom</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody>
            {cities.map((city, index) => (
              <tr key={city.id}>
                <th scope="row">{index + 1}</th>
                <td>{city.nom}</td>
                <td>
                  <button className="btn btn-primary btn-sm mx-1" onClick={() => getCityById(city.id)}>
                    Edit
                  </button>
                  <button className="btn btn-danger btn-sm" onClick={() => deleteCity(city.id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="mt-3 ">
          <input
            type="text"
            id="cityname"
            className="form-control"
            placeholder="City Name"
            value={cityNom}
            onChange={(e) => setCityNom(e.target.value)}
          />
          {cityId ? (
            <button className="btn btn-primary mt-2" onClick={() => updateCity(cityId)}>
              Update
            </button>
          ) : (
            <button className="btn btn-primary mt-2" onClick={addCity}>
              Add
            </button>
          )}
        </div>
      </div>
    </Fragment>
  );
};

export default City;
