import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const Zone = () => {
  const [zones, setZones] = useState([]);
  const [zoneNom, setZoneNom] = useState('');
  const [zoneVilleId, setZoneVilleId] = useState('');
  const [cities, setCities] = useState([]);
  const [zoneId, setZoneId] = useState('');

  // Function to fetch the list of zones from the backend
  const fetchZones = async () => {
    try {
      const response = await axios.get('/api/zones/all');
      setZones(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to fetch the list of cities from the backend
  const fetchCities = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setCities(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to get a zone by ID
  const getZoneById = async (zoneId) => {
    try {
      const response = await axios.get(`/zone/${zoneId}`);
      const zone = zones.find((zone) => zone.id === zoneId);
      setZoneNom(response.data.nom);
      setZoneVilleId(response.data.ville_id);
      setZoneId(zoneId);
      document.getElementById('zonename').value = response.data.nom;
      document.getElementById('cityselect').value = response.data.ville_id;
    } catch (error) {
      console.error(error);
    }
  };

  // Function to add a zone
  const addZone = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/zones/save', {
        nom: zoneNom,
        ville_id: zoneVilleId,
      });
      setZones([...zones, response.data]);
      setZoneNom('');
      setZoneVilleId('');
    } catch (error) {
      console.error(error);
    }
    fetchZones();
  };

  // Function to delete a zone
  const deleteZone = async (id) => {
    try {
      await axios.delete(`/api/zones/delete/${id}`);
      const updatedZones = zones.filter((zone) => zone.id !== id);
      setZones(updatedZones);
    } catch (error) {
      console.error(error);
    }
    fetchZones();
  };

  // Function to update a zone
  const updateZone = async (zoneId) => {
    try {
      const response = await axios.put(`/zone/update/${zoneId}`, {
        nom: zoneNom,
        ville_id: zoneVilleId,
      });
      const updatedZones = zones.map((zone) => {
        if (zone.id === response.data.id) {
          return response.data;
        }
        return zone;
      });
      setZoneNom('');
      setZoneVilleId('');
      setZoneId('');
      setZones(updatedZones);
    } catch (error) {
      console.error(error);
    }
    fetchZones();
    document.getElementById('zonename').value = '';
    document.getElementById('cityselect').value = '';
  };

  useEffect(() => {
    fetchZones();
    fetchCities();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Zones</h2>
      <table className="table table-bordered mt-3">
        <thead>
          <tr>
            <th scope="col">No.</th>
            <th scope="col">Nom</th>
            <th scope="col">Ville ID</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {zones.map((zone, index) => (
            <tr key={zone.id}>
              <th scope="row">{index + 1}</th>
              <td>{zone.nom}</td>
              <td>{zone.ville_id}</td>
              <td>
                <button
                  className="btn btn-sm btn-primary"
                  onClick={() => getZoneById(zone.id)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-sm btn-danger ml-2"
                  onClick={() => deleteZone(zone.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h3>Add/Edit Zone</h3>
      <form>
        <div className="form-group">
          <label htmlFor="zonename">Nom</label>
          <input
            type="text"
            className="form-control"
            id="zonename"
            value={zoneNom}
            onChange={(e) => setZoneNom(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="cityselect">Ville</label>
          <select
            className="form-control"
            id="cityselect"
            value={zoneVilleId}
            onChange={(e) => setZoneVilleId(e.target.value)}
          >
            <option value="">Select a city</option>
            {cities.map((city) => (
              <option key={city.id} value={city.id}>
                {city.name}
              </option>
            ))}
          </select>
        </div>
        {zoneId ? (
          <button
            className="btn btn-primary"
            onClick={() => updateZone(zoneId)}
          >
            Update Zone
          </button>
        ) : (
          <button className="btn btn-primary" onClick={addZone}>
            Add Zone
          </button>
        )}
      </form>
    </div>
  );
};

export default Zone;
