import React, { useState, useEffect } from 'react';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';
import { BsSearch } from 'react-icons/bs';
import './App.css';

const Search = () => {
  const [searchValue, setSearchValue] = useState('');
  const [pharmacies, setPharmacies] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`/api/pharmacies/byZone/${searchValue}`);
      setPharmacies(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePharmacieClick = (pharmacieId) => {
    // Handle the logic for displaying details of the pharmacy with the given ID
    console.log(`Displaying details of pharmacy with ID ${pharmacieId}`);
  };

  return (
    <div className="container mt-4">
      <h1>Search Pharmacies</h1>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search by zone ID"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
        />
        <button className="btn btn-primary" onClick={handleSearch}>
          <BsSearch />
        </button>
      </div>
      <h2>Search Results</h2>
      {pharmacies.length > 0 ? (
        <ul>
          {pharmacies.map((pharmacie) => (
            <li key={pharmacie.id} onClick={() => handlePharmacieClick(pharmacie.id)}>
              {pharmacie.nom}
            </li>
          ))}
        </ul>
      ) : (
        <p>No pharmacies found for the given zone ID.</p>
      )}
    </div>
  );
};

export default Search;
