import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleMap, Marker } from '@react-google-maps/api';

const Search = () => {
  const [villes, setVilles] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');
  const [pharmacies, setPharmacies] = useState([]);
  const [selectedPharmacyPosition, setSelectedPharmacyPosition] = useState(null);

  useEffect(() => {
    loadVilles();
  }, []);

  const loadVilles = async () => {
    const response = await axios.get('/api/villes/all');
    setVilles(response.data);
  };

  const loadZonesByVille = async () => {
    const response = await axios.get(`/api/villes/${selectedVille}/zones`);
    setZones(response.data);
  };

  const loadPharmaciesByZone = async () => {
    const response = await axios.get(`/api/pharmacies/byZone/${selectedZone}`);
    setPharmacies(response.data);
  };

  const handleVilleChange = (e) => {
    const selectedVille = e.target.value;
    setSelectedVille(selectedVille);
    setSelectedZone('');
    setZones([]);
    setPharmacies([]);
    if (selectedVille) {
      loadZonesByVille();
    }
  };

  const handleZoneChange = (e) => {
    const selectedZone = e.target.value;
    setSelectedZone(selectedZone);
    setPharmacies([]);
    if (selectedZone) {
      loadPharmaciesByZone();
    }
  };

  const handlePharmacyClick = (pharmacy) => {
    setSelectedPharmacyPosition({
      lat: parseFloat(pharmacy.latitude),
      lng: parseFloat(pharmacy.longitude),
    });
  };

  return (
    <div>
      <h2>Search Pharmacies</h2>
      <div>
        <label htmlFor="villeSelect">Select Ville:</label>
        <select id="villeSelect" value={selectedVille} onChange={handleVilleChange}>
          <option value="">-- Select Ville --</option>
          {villes.map((ville) => (
            <option key={ville.id} value={ville.nom}>
              {ville.nom}
            </option>
          ))}
        </select>
      </div>
      {selectedVille && (
        <div>
          <label htmlFor="zoneSelect">Select Zone:</label>
          <select id="zoneSelect" value={selectedZone} onChange={handleZoneChange}>
            <option value="">-- Select Zone --</option>
            {zones.map((zone) => (
              <option key={zone.id} value={zone.id}>
                {zone.nom}
              </option>
            ))}
          </select>
        </div>
      )}
      {selectedZone && (
        <div>
          <h3>Pharmacies:</h3>
          <ul>
            {pharmacies.map((pharmacie) => (
              <li key={pharmacie.id} onClick={() => handlePharmacyClick(pharmacie)}>
                {pharmacie.nom}
              </li>
            ))}
          </ul>
        </div>
      )}
      <div>
        <h3>Map:</h3>
        <div id="map-container">
          <GoogleMap
            center={selectedPharmacyPosition}
            zoom={12}
            mapContainerStyle={{ height: '400px', width: '100%' }}
          >
            {selectedPharmacyPosition && <Marker position={selectedPharmacyPosition} />}
          </GoogleMap>
        </div>
      </div>
    </div>
  );
};

export default Search;
