import React, { useState } from 'react';
import { FaEdit, FaPlus, FaTrash } from 'react-icons/fa';
const Gardes = () => {
  const [gardes, setGardes] = useState([]);
  const [gardeName, setGardeName] = useState('');
  const [editingGarde, setEditingGarde] = useState(null);

  const addGarde = (event) => {
    event.preventDefault();
    const newGarde = { name: gardeName };
    setGardes([...gardes, newGarde]);
    setGardeName('');
  };

  const deleteGarde = (gardeIndex) => {
    const updatedGardes = [...gardes];
    updatedGardes.splice(gardeIndex, 1);
    setGardes(updatedGardes);
  };

  const editGarde = (gardeIndex) => {
    const gardeToEdit = gardes[gardeIndex];
    setEditingGarde(gardeIndex);
    setGardeName(gardeToEdit.name);
  };

  const updateGarde = (event) => {
    event.preventDefault();
    const updatedGardes = [...gardes];
    updatedGardes[editingGarde] = { name: gardeName };
    setGardes(updatedGardes);
    setEditingGarde(null);
    setGardeName('');
  };

  return (
    <div>
      <h2>Garde</h2>
      {!editingGarde ? (
        <form onSubmit={addGarde}>
          <label htmlFor="gardeName">Nom:</label>
          <input
            type="text"
            id="gardeName"
            value={gardeName}
            required
            onChange={(event) => setGardeName(event.target.value)}
          />
          <button type="submit"> <FaPlus/></button>
        </form>
      ) : (
        <form onSubmit={updateGarde}>
          <label htmlFor="gardeName">Nom:</label>
          <input
            type="text"
            id="gardeName"
            value={gardeName}
            required
            onChange={(event) => setGardeName(event.target.value)}
          />
          <button type="submit">Mettre Ã  jour</button>
          <button onClick={() => setEditingGarde(null)}>Annuler</button>
        </form>
      )}

      <h2>Liste des gardes</h2>
      <table>
        <thead>
          <tr>
            <th>Nom</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {gardes.map((garde, index) => (
            <tr key={index}>
              <td>{garde.name}</td>
              <td>
                <button onClick={() => deleteGarde(index)}> <FaTrash className="icon-lg" /></button>
                <button onClick={() => editGarde(index)}><FaEdit /></button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Gardes;
