import React, { useState, useEffect } from 'react';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';
import { BsSearch } from 'react-icons/bs';
import './App.css';

const Search = () => {
  const [villeOptions, setVilleOptions] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [zoneOptions, setZoneOptions] = useState([]);
  const [selectedZone, setSelectedZone] = useState('');
  const [pharmacies, setPharmacies] = useState([]);

  useEffect(() => {
    fetchVilles();
  }, []);

  const fetchVilles = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setVilleOptions(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchZonesByVille = async (villeId) => {
    try {
      const response = await axios.get(`/api/zones/byVille/${villeId}`);
      setZoneOptions(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchPharmaciesByZone = async (zoneId) => {
    try {
      const response = await axios.get(`/api/pharmacies/byZone/${zoneId}`);
      setPharmacies(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleVilleChange = (event) => {
    const selectedVille = event.target.value;
    setSelectedVille(selectedVille);
    setSelectedZone('');
    setZoneOptions([]);
    setPharmacies([]);
    fetchZonesByVille(selectedVille);
  };

  const handleZoneChange = (event) => {
    const selectedZone = event.target.value;
    setSelectedZone(selectedZone);
    fetchPharmaciesByZone(selectedZone);
  };

  const handlePharmacieClick = (pharmacieId) => {
    // Handle the logic for displaying details of the pharmacy with the given ID
    console.log(`Displaying details of pharmacy with ID ${pharmacieId}`);
  };

  return (
    <div className="container mt-4">
      <h1>Search Pharmacies</h1>
      <div className="form-group">
        <label htmlFor="villeSelect">Select Ville:</label>
        <select className="form-control" id="villeSelect" value={selectedVille} onChange={handleVilleChange}>
          <option value="">-- Select Ville --</option>
          {villeOptions.map((ville) => (
            <option key={ville.id} value={ville.id}>
              {ville.name}
            </option>
          ))}
        </select>
      </div>
      {selectedVille && (
        <div className="form-group">
          <label htmlFor="zoneSelect">Select Zone:</label>
          <select className="form-control" id="zoneSelect" value={selectedZone} onChange={handleZoneChange}>
            <option value="">-- Select Zone --</option>
            {zoneOptions.map((zone) => (
              <option key={zone.id} value={zone.id}>
                {zone.name}
              </option>
            ))}
          </select>
        </div>
      )}
      {pharmacies.length > 0 ? (
        <div>
          <h2>Pharmacies</h2>
          <ul>
            {pharmacies.map((pharmacie) => (
              <li key={pharmacie.id} onClick={() => handlePharmacieClick(pharmacie.id)}>
                {pharmacie.nom}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No pharmacies found.</p>
      )}
    </div>
  );
};

export default Search;
