import React, { useState, useEffect } from 'react';

const Pharmacy = () => {
  const [pharmacies, setPharmacies] = useState([]);
  const [pharmacieNom, setPharmacieNom] = useState('');
  const [pharmacieAdresse, setPharmacieAdresse] = useState('');
  const [pharmacieLatitude, setPharmacieLatitude] = useState('');
  const [pharmacieLongitude, setPharmacieLongitude] = useState('');
  const [zoneId, setZoneId] = useState('');
  const [zones, setZones] = useState([]);
  const [editingPharmacieId, setEditingPharmacieId] = useState('');

  useEffect(() => {
    fetchPharmacies();
    fetchZones();
  }, []);

  const fetchPharmacies = () => {
    // Replace with actual API call to fetch pharmacies
    const dummyPharmacies = [
      {
        id: 1,
        nom: 'Pharmacy 1',
        adresse: 'Address 1',
        latitude: '123.456',
        longitude: '789.012',
        zone: { id: 1, name: 'Zone 1' }
      },
      {
        id: 2,
        nom: 'Pharmacy 2',
        adresse: 'Address 2',
        latitude: '345.678',
        longitude: '901.234',
        zone: { id: 2, name: 'Zone 2' }
      }
    ];

    setPharmacies(dummyPharmacies);
  };

  const fetchZones = () => {
    // Replace with actual API call to fetch zones
    const dummyZones = [
      { id: 1, name: 'Zone 1' },
      { id: 2, name: 'Zone 2' },
      { id: 3, name: 'Zone 3' }
    ];

    setZones(dummyZones);
  };

  const savePharmacy = () => {
    if (editingPharmacieId) {
      // Perform update API call
      console.log(`Updating pharmacy with ID ${editingPharmacieId}`);
    } else {
      // Perform create API call
      console.log('Creating new pharmacy');
    }

    // Reset form values
    setPharmacieNom('');
    setPharmacieAdresse('');
    setPharmacieLatitude('');
    setPharmacieLongitude('');
    setZoneId('');
    setEditingPharmacieId('');
  };

  const deletePharmacy = (pharmacyId) => {
    // Perform delete API call
    console.log(`Deleting pharmacy with ID ${pharmacyId}`);
  };

  const editPharmacy = (pharmacy) => {
    setPharmacieNom(pharmacy.nom);
    setPharmacieAdresse(pharmacy.adresse);
    setPharmacieLatitude(pharmacy.latitude);
    setPharmacieLongitude(pharmacy.longitude);
    setZoneId(pharmacy.zone ? pharmacy.zone.id : '');
    setEditingPharmacieId(pharmacy.id);
  };

  return (
    <div className="container mt-4">
      <h1>Pharmacies</h1>

      <div className="form-group">
        <label htmlFor="nom">Nom:</label>
        <input
          type="text"
          className="form-control"
          id="nom"
          value={pharmacieNom}
          onChange={(e) => setPharmacieNom(e.target.value)}
        />
      </div>

      <div className="form-group">
        <label htmlFor="adresse">Adresse:</label>
        <input
          type="text"
          className="form-control"
          id="adresse"
          value={pharmacieAdresse}
          onChange={(e) => setPharmacieAdresse(e.target.value)}
        />
      </div>

      <div className="form-group">
        <label htmlFor="latitude">Latitude:</label>
        <input
          type="text"
          className="form-control"
          id="latitude"
          value={pharmacieLatitude}
          onChange={(e) => setPharmacieLatitude(e.target.value)}
        />
      </div>

      <div className="form-group">
        <label htmlFor="longitude">Longitude:</label>
        <input
          type="text"
          className="form-control"
          id="longitude"
          value={pharmacieLongitude}
          onChange={(e) => setPharmacieLongitude(e.target.value)}
        />
      </div>

      <div className="form-group">
        <label htmlFor="zone">Zone:</label>
        <select
          className="form-control"
          id="zone"
          value={zoneId}
          onChange={(e) => setZoneId(e.target.value)}
        >
          <option value="">Select a zone</option>
          {zones.map((zone) => (
            <option key={zone.id} value={zone.id}>
              {zone.name}
            </option>
          ))}
        </select>
      </div>

      <button className="btn btn-primary" onClick={savePharmacy}>
        {editingPharmacieId ? 'Update Pharmacy' : 'Add Pharmacy'}
      </button>

      <table className="table mt-4">
        <thead>
          <tr>
            <th>Nom</th>
            <th>Adresse</th>
            <th>Latitude</th>
            <th>Longitude</th>
            <th>Zone</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pharmacies.map((pharmacie) => (
            <tr key={pharmacie.id}>
              <td>{pharmacie.nom}</td>
              <td>{pharmacie.adresse}</td>
              <td>{pharmacie.latitude}</td>
              <td>{pharmacie.longitude}</td>
              <td>{pharmacie.zone ? pharmacie.zone.name : ''}</td>
              <td>
                <button
                  className="btn btn-sm btn-primary mr-2"
                  onClick={() => editPharmacy(pharmacie)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => deletePharmacy(pharmacie.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Pharmacy;
