import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import ZoneManagementComponent from './ZoneManagementComponent';
import { BrowserRouter, Switch, Route, Link } from 'react-router-dom';

const City = () => {
  const [cities, setCities] = useState([]);
  const [cityNom, setCityNom] = useState('');
  const [cityId, setCityId] = useState('');

  // Function to fetch the list of cities from the backend x
  const fetchCities = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setCities(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to get a city by ID
  const getCityById = async (cityId) => {
    try {
      const response = await axios.get(`/ville/${cityId}`);
      const cit = cities.find((city) => city.id === cityId);
      setCityNom(response.data.nom);
      setCityId(response.data.id);
      document.getElementById('cityname').value = response.data.nom;
    } catch (error) {
      console.error(error);
    }
  };

  // Function to add a city x
  const addCity = async () => {
    try {
      const response = await axios.post('/api/villes/save', { nom: cityNom });
      setCities([...cities, response.data]);
      setCityNom('');
    } catch (error) {
      console.error(error);
    }
    fetchCities();
  };

  // Function to delete a city x
  const deleteCity = async (id) => {
    try {
      await axios.delete(`/api/villes/delete/${id}`);
      const updatedCities = cities.filter((city) => city.id !== id);
      setCities(updatedCities);
    } catch (error) {
      console.error(error);
    }
    fetchCities();
  };

  // Function to update a city
  const updateCity = async (cityId) => {
    try {
      const response = await axios.put(`/ville/update/${cityId}`, { nom: cityNom });
      const updatedCities = cities.map((city) => {
        if (city.id === response.data.id) {
          return response.data;
        }
        return city;
      });
      setCityId('');
      setCityNom('');
      setCities(updatedCities);
    } catch (error) {
      console.error(error);
    }
    fetchCities();
    document.getElementById('cityname').value = '';
  };

  useEffect(() => {
    fetchCities();
  }, []);

  return (
    <div className="container mt-4">
      <h1>Cities</h1>
      <table className="table mt-3">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nom</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {cities.map((city, index) => (
            <tr key={city.id}>
              <th scope="row">{index + 1}</th>
              <td>{city.nom}</td>
              <td>
                <button className="btn btn-primary btn-sm mx-1" onClick={() => getCityById(city.id)}>
                  Edit
                </button>
                <button className="btn btn-danger btn-sm" onClick={() => deleteCity(city.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mt-3">

  <input
    type="text"
    className="form-control mr-2 d-inline-block"
    id="cityname"
    onChange={(e) => setCityNom(e.target.value)}
  />
  {cityId ? (
    <button className="btn btn-success" onClick={() => updateCity(cityId)}>
      Update City
    </button>
  ) : (
    <button className="btn btn-primary" onClick={addCity}>
      Add City
    </button>
  )}
</div>
</div>
);
};

export default City;
