import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

const City = () => {
  const [cities, setCities] = useState([]);
  const [cityNom, setCityNom] = useState('');
  const [cityId, setCityId] = useState('');

  // Function to fetch the list of cities from the backend
  const fetchCities = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setCities(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to get a city by ID
  const getCityById = async (cityId) => {
    try {
      const response = await axios.get(`/ville/${cityId}`);
      const cit = cities.find((city) => city.id === cityId);
      setCityNom(response.data.nom);
      setCityId(response.data.id);
      document.getElementById('cityname').value = response.data.nom;
    } catch (error) {
      console.error(error);
    }
  };

  // Function to add a city
  const addCity = async () => {
    try {
      const response = await axios.post('/api/villes/save', { nom: cityNom });
      setCities([...cities, response.data]);
      setCityNom('');
    } catch (error) {
      console.error(error);
    }
    fetchCities();
  };

  // Function to delete a city
  const deleteCity = async (id) => {
    try {
      await axios.delete(`/api/villes/delete/${id}`);
      const updatedCities = cities.filter((city) => city.id !== id);
      setCities(updatedCities);
    } catch (error) {
      console.error(error);
    }
    fetchCities();
  };

  // Function to update a city
  const updateCity = async (cityId) => {
    try {
      const response = await axios.put(`/ville/update/${cityId}`, { nom: cityNom });
      const updatedCities = cities.map((city) => {
        if (city.id === response.data.id) {
          return response.data;
        }
        return city;
      });
      setCityId('');
      setCityNom('');
      setCities(updatedCities);
    } catch (error) {
      console.error(error);
    }
    fetchCities();
    document.getElementById('cityname').value = '';
  };

  useEffect(() => {
    fetchCities();
  }, []);

  return (
    <div className="container mt-4">
      <h1>Cities1</h1> {/* Updated content */}
      <table className="table mt-3">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nom</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
          {cities.map((city, index) => (
            <tr key={city.id}>
              <th scope="row">{index + 1}</th>
              <td>{city.nom}</td>
              <td>
                <button className="btn btn-primary btn-sm mx-1" onClick={() => getCityById(city.id)}>
                  Edit
                </button>
                <button className="btn btn-danger btn-sm" onClick={() => deleteCity(city.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="mt-3">
        <input
          type="text"
          id="cityname"
          className="form-control"
          placeholder="City Name"
          value={cityNom}
          onChange={(e) => setCityNom(e.target.value)}
        />
        {cityId ? (
          <button className="btn btn-primary mt-2" onClick={() => updateCity(cityId)}>
            Update
          </button>
        ) : (
          <button className="btn btn-primary mt-2" onClick={addCity}>
            Add
          </button>
        )}
      </div>
    </div>
  );
};

const Navbar = () => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container">
        <Link to="/" className="navbar-brand">Home</Link>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">

          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link to="/cities" className="nav-link">Cities</Link>
            </li>

            <li className="nav-item">
              <Link to="/cities" className="nav-link">Cities</Link>
            </li>
            {/* Add more navigation items as needed */}
          </ul>
        </div>
      </div>
    </nav>
  );
};

const App = () => {
  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route path="/" element={<City />} />
        <Route path="/cities" element={<h1>Cities Page</h1>} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
