import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleMap, Marker, LoadScript } from '@react-google-maps/api';
import './App.css';

const Search = () => {
  const [villes, setVilles] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');
  const [pharmacies, setPharmacies] = useState([]);
  const [selectedPharmacyPosition, setSelectedPharmacyPosition] = useState(null);

  useEffect(() => {
    fetchVilles();
  }, []);

  useEffect(() => {
    if (selectedVille) {
      fetchZonesByVille();
    }
  }, [selectedVille]);

  const fetchVilles = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setVilles(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchZonesByVille = async () => {
    try {
      const response = await axios.get(`/api/villes/${selectedVille}/zones`);
      setZones(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const searchPharmacies = async () => {
    try {
      const response = await axios.get(`/api/pharmacies/byZone/${selectedZone}`);
      setPharmacies(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleVilleChange = (e) => {
    setSelectedVille(e.target.value);
    setSelectedZone('');
    setZones([]);
  };

  const handleZoneChange = (e) => {
    setSelectedZone(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedZone) {
      searchPharmacies();
    }
  };

  const handlePharmacyClick = (pharmacy) => {
    setSelectedPharmacyPosition({
      lat: parseFloat(pharmacy.latitude),
      lng: parseFloat(pharmacy.longitude),
    });
  };

  return (
    <div>
      <h2 style="color: red; font-size: 16px">Search Pharmacies</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="ville">Ville:</label>
          <select id="ville" value={selectedVille} onChange={handleVilleChange}>
            <option value="">Select Ville</option>
            {villes.map((ville) => (
              <option key={ville.id} value={ville.nom}>
                {ville.nom}
              </option>
            ))}
          </select>
        </div>
        {selectedVille && (
          <div>
            <label htmlFor="zone">Zone:</label>
            <select id="zone" value={selectedZone} onChange={handleZoneChange}>
              <option value="">Select Zone</option>
              {zones.map((zone) => (
                <option key={zone.id} value={zone.id}>
                  {zone.nom}
                </option>
              ))}
            </select>
          </div>
        )}
        <button type="submit" disabled={!selectedZone}>
          Search
        </button>
      </form>

      {pharmacies.length > 0 && (
        <div>
          <h3>Pharmacies:</h3>
          <ul>
            {pharmacies.map((pharmacie) => (
              <li key={pharmacie.id}>{pharmacie.nom}</li>
            ))}
          </ul>
        </div>
      )}

      <div>
        <h3>Map:</h3>
        {selectedPharmacyPosition && (
          <LoadScript googleMapsApiKey="YOUR_API_KEY">
            <GoogleMap
              center={selectedPharmacyPosition}
              zoom={12}
              mapContainerStyle={{ height: '400px', width: '100%' }}
            >
              {pharmacies.map((pharmacie) => (
                <Marker
                  key={pharmacie.id}
                  position={{ lat: parseFloat(pharmacie.latitude), lng: parseFloat(pharmacie.longitude) }}
                  onClick={() => handlePharmacyClick(pharmacie)}
                />
              ))}
            </GoogleMap>
          </LoadScript>
        )}
      </div>
    </div>
  );
};

export default Search;
