import React, { useState, useEffect } from 'react';
import axios from 'axios';

// MapComponent.js
const MapComponent = ({ longitude, latitude }) => {
  useEffect(() => {
    // Initialize map and marker using the map library or API

    // Set map options and center
    const mapOptions = {
      center: [latitude, longitude],
      zoom: 12,
    };

    // Create a marker at the specified position
    const marker = new Marker([latitude, longitude]);

    // Add the marker to the map

    // Render the map container
    // Replace "map-container" with the ID or class name of the map container element
    const mapContainer = document.getElementById('map-container');
    // Render the map with the specified options
    const map = new Map(mapContainer, mapOptions);

    // Add the marker to the map
    map.addMarker(marker);
  }, [longitude, latitude]);

  return <div id="map-container" style={{ height: '400px' }}></div>;
};

const Search = () => {
  const [villes, setVilles] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');
  const [pharmacies, setPharmacies] = useState([]);
  const [selectedPharmacy, setSelectedPharmacy] = useState(null);

  useEffect(() => {
    fetchVilles();
  }, []);

  useEffect(() => {
    if (selectedVille) {
      fetchZonesByVille();
    }
  }, [selectedVille]);

  const fetchVilles = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setVilles(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchZonesByVille = async () => {
    try {
      const response = await axios.get(`/api/villes/${selectedVille}/zones`);
      setZones(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const searchPharmacies = async () => {
    try {
      const response = await axios.get(`/api/pharmacies/byZone/${selectedZone}`);
      setPharmacies(response.data);
      setSelectedPharmacy(null); // Reset selected pharmacy
    } catch (error) {
      console.error(error);
    }
  };

  const handleVilleChange = (e) => {
    setSelectedVille(e.target.value);
    setSelectedZone('');
    setZones([]);
  };

  const handleZoneChange = (e) => {
    setSelectedZone(e.target.value);
  };

  const handlePharmacyClick = (pharmacy) => {
    setSelectedPharmacy(pharmacy);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedZone) {
      searchPharmacies();
    }
  };

  return (
    <div>
      <h2>Search Pharmacies</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="ville">Ville:</label>
          <select id="ville" value={selectedVille} onChange={handleVilleChange}>
            <option value="">Select Ville</option>
            {villes.map((ville) => (
              <option key={ville.id} value={ville.nom}>
                {ville.nom}
              </option>
            ))}
          </select>
        </div>
        {selectedVille && (
          <div>
            <label htmlFor="zone">Zone:</label>
            <select id="zone" value={selectedZone} onChange={handleZoneChange}>
              <option value="">Select Zone</option>
              {zones.map((zone) => (
                <option key={zone.id} value={zone.id}>
                  {zone.nom}
                </option>
              ))}
            </select>
          </div>
        )}
        <button type="submit" disabled={!selectedZone}>
          Search
        </button>
      </form>

      {pharmacies.length > 0 && (
        <div>
          <h3>Pharmacies:</h3>
          <ul>
            {pharmacies.map((pharmacie) => (
              <li key={pharmacie.id} onClick={() => handlePharmacyClick(pharmacie)}>
                {pharmacie.nom}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedPharmacy && (
        <div>
          <h3>Selected Pharmacy:</h3>
          <p>Pharmacy Name: {selectedPharmacy.nom}</p>
          <p>Longitude: {selectedPharmacy.longitude}</p>
          <p>Latitude: {selectedPharmacy.latitude}</p>
          <MapComponent longitude={selectedPharmacy.longitude} latitude={selectedPharmacy.latitude} />
        </div>
      )}
    </div>
  );
};

export default Search;
