import React, { useState, useEffect } from 'react';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';

const Zone = () => {
  const [zones, setZones] = useState([]);
  const [zoneNom, setZoneNom] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [cities, setCities] = useState([]);

  // Function to fetch the list of zones from the backend
  const fetchZones = async () => {
    try {
      const response = await axios.get('/api/zones/all');
      setZones(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to fetch the list of cities from the backend
  const fetchCities = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setCities(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  // Function to add a zone
  const addZone = async () => {
    try {
      const response = await axios.post('/api/zones/save', { nom: zoneNom, cityId: selectedCity });
      setZones([...zones, response.data]);
      setZoneNom('');
      setSelectedCity('');
    } catch (error) {
      console.error(error);
    }
    fetchZones();
  };

  // Function to delete a zone
  const deleteZone = async (id) => {
    try {
      await axios.delete(`/api/zones/delete/${id}`);
      const updatedZones = zones.filter((zone) => zone.id !== id);
      setZones(updatedZones);
    } catch (error) {
      console.error(error);
    }
    fetchZones();
  };

  useEffect(() => {
    fetchZones();
    fetchCities();
  }, []);

  return (
    <div className="container mt-4">
      <h1>Zones</h1>
      <table className="table mt-3">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nom</th>
            <th scope="col">City</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
        {zones.map((zone, index) => (
  <tr key={zone.id}>
    <th scope="row">{index + 1}</th>
    <td>{zone.nom ? zone.nom : ''}</td>
    <td>{zone.ville ? zone.ville.nom : ''}</td>
    <td>
      <button
        className="btn btn-danger btn-sm zoom-on-hover"
        onClick={() => deleteZone(zone.id)}
      >
        Delete
      </button>
    </td>
  </tr>
))}
        </tbody>
      </table>
      <div className="mt-3">
        <input
          type="text"
          className="form-control"
          placeholder="Zone Name"
          value={zoneNom}
          onChange={(e) => setZoneNom(e.target.value)}
        />
        <select
          className="form-control mt-2"
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
        >
          <option value="">Select City</option>
          {cities.map((city) => (
            <option key={city.id} value={city.id}>
              {city.nom}
            </option>
          ))}
        </select>
        <button className="btn btn-primary mt-2" onClick={addZone}>
          Add
        </button>
      </div>
    </div>
  );
};

export default Zone;
