import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleMap, Marker, LoadScript } from '@react-google-maps/api';
import './App.css';

const Search = () => {
  const [villes, setVilles] = useState([]);
  const [zones, setZones] = useState([]);
  const [selectedVille, setSelectedVille] = useState('');
  const [selectedZone, setSelectedZone] = useState('');
  const [pharmacies, setPharmacies] = useState([]);
  const [selectedPharmacy, setSelectedPharmacy] = useState(null); // New state variable
  const [selectedPharmacyPosition, setSelectedPharmacyPosition] = useState(null);

  useEffect(() => {
    fetchVilles();
  }, []);

  useEffect(() => {
    if (selectedVille) {
      fetchZonesByVille();
    }
  }, [selectedVille]);

  const fetchVilles = async () => {
    try {
      const response = await axios.get('/api/villes/all');
      setVilles(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchZonesByVille = async () => {
    try {
      const response = await axios.get(`/api/villes/${selectedVille}/zones`);
      setZones(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const searchPharmacies = async () => {
    try {
      const response = await axios.get(`/api/pharmacies/byZone/${selectedZone}`);
      setPharmacies(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleVilleChange = (e) => {
    setSelectedVille(e.target.value);
    setSelectedZone('');
    setZones([]);
  };

  const handleZoneChange = (e) => {
    setSelectedZone(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedZone) {
      searchPharmacies();
    }
  };

  const handlePharmacyClick = (pharmacy) => {
    setSelectedPharmacy(pharmacy); // Store the selected pharmacy
    setSelectedPharmacyPosition({
      lat: parseFloat(pharmacy.latitude),
      lng: parseFloat(pharmacy.longitude),
    });
  };

  return (
    <div className="container mt-4">
      <h2 className="text-g font-big1 slide-in-top">recherche des pharmacies</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
      </form>

      {pharmacies.length > 0 && (
        <div>
          <h3 className="font-m">Pharmacies:</h3>
          <ul className="pharmacy-list">
            {/* Display pharmacy buttons */}
            {pharmacies.map((pharmacy) => (
              <li key={pharmacy.id}>
                <button onClick={() => handlePharmacyClick(pharmacy)}>
                  {pharmacy.nom}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      <div>
        <h3 className="font-m">Localisation:</h3>
        <LoadScript googleMapsApiKey="YOUR_API_KEY">
          <GoogleMap
            center={selectedPharmacyPosition || { lat: 0, lng: 0 }} // Set a default center if no pharmacy is selected
            zoom={12}
            mapContainerStyle={{ height: '400px', width: '100%' }}
          >
            {/* Display markers for pharmacies */}
            {pharmacies.map((pharmacy) => (
              <Marker
                key={pharmacy.id}
                position={{ lat: parseFloat(pharmacy.latitude), lng: parseFloat(pharmacy.longitude) }}
                onClick={() => handlePharmacyClick(pharmacy)}
              />
            ))}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default Search;
